 7→@Mockable
 8→public protocol CacheDirectoriesProviding {
 9→    /// Returns the cache directory for a Tuist cache category
10→    func cacheDirectory(for category: CacheCategory) throws -> AbsolutePath
11→    func cacheDirectory() -> AbsolutePath
12→}
13→
14→public final class CacheDirectoriesProvider: CacheDirectoriesProviding {
15→    public init() {}
16→
17→    public func cacheDirectory(for category: CacheCategory) throws -> AbsolutePath {
18→        cacheDirectory().appending(component: category.directoryName)
19→    }
20→
21→    public func cacheDirectory() -> Path.AbsolutePath {
22→        Environment.current.cacheDirectory
23→    }
24→
25→    public static func bootstrap() async throws {
26→        let fileSystem = FileSystem()
27→        let provider = CacheDirectoriesProvider()
28→        for category in CacheCategory.allCases {
29→            let directory = try provider.cacheDirectory(for: category)
30→            if try await !fileSystem.exists(directory) {
31→                try await fileSystem.makeDirectory(at: directory)
32→            }
33→        }
34→    }
35→}
