import FileSystem
import Foundation
import Path
import TuistAlert
import TuistCache
import TuistCore
import TuistLogging
import TuistServer
import TuistSupport
import XcodeGraph

enum CacheLocalStorageError: FatalError, Equatable {
    case compiledArtifactNotFound(hash: String)

    var type: ErrorType {
        switch self {
        case .compiledArtifactNotFound: return .abort
        }
    }

    var description: String {
        switch self {
        case let .compiledArtifactNotFound(hash):
            return "xcframework with hash '\(hash)' not found in the local cache"
        }
    }
}

public struct CacheLocalStorage: CacheStoring {
    // MARK: - Attributes

    private let cacheDirectoriesProvider: CacheDirectoriesProviding
    private let artifactSigner: ArtifactSigning
    private let fileHandler: FileHandling
    private let fileSystem: FileSysteming

    // MARK: - Init

    public init(cacheDirectoriesProvider: CacheDirectoriesProviding) {
        self.init(
            cacheDirectoriesProvider: cacheDirectoriesProvider,
            artifactSigner: ArtifactSigner(),
            fileHandler: FileHandler.shared,
            fileSystem: FileSystem()
        )
    }

    init(
        cacheDirectoriesProvider: CacheDirectoriesProviding,
        artifactSigner: ArtifactSigning,
        fileHandler: FileHandling,
        fileSystem: FileSysteming
    ) {
        self.cacheDirectoriesProvider = cacheDirectoriesProvider
        self.artifactSigner = artifactSigner
        self.fileHandler = fileHandler
        self.fileSystem = fileSystem
    }

    // MARK: - CacheStoring

    public func fetch(
        _ items: Set<CacheStorableItem>,
        cacheCategory: RemoteCacheCategory
    ) async throws -> [CacheItem: AbsolutePath] {
        let results = Dictionary(uniqueKeysWithValues: try await Array(items).concurrentCompactMap { item -> (
            CacheItem,
            AbsolutePath
        )? in
            let cacheDirectory = try cacheDirectory(for: cacheCategory)
            let hashPath = cacheDirectory.appending(component: item.hash)
            guard try await isValidAndExists(
                hashPath: hashPath,
                name: item.name,
                cacheCategory: cacheCategory
            ) else {
                return nil
            }

            var buildDuration: Double?
            switch cacheCategory {
            case .binaries:
                let metadataFilePath = hashPath.appending(component: "Metadata.plist")
                if try await fileSystem.exists(metadataFilePath) {
                    let metadata: CacheStorableItemMetadata? = try? await fileSystem.readPlistFile(at: metadataFilePath)
                    buildDuration = metadata?.time
                }
            case .selectiveTests:
                break
            }

            let cacheItem = CacheItem(
                name: item.name,
                hash: item.hash,
                source: .local,
                cacheCategory: cacheCategory,
                buildDuration: buildDuration
            )

            if cacheCategory == .selectiveTests {
                return try await fileSystem.exists(hashPath) ? (cacheItem, hashPath) : nil
            }

            if let path = try await lookupCompiledArtifact(
                directory: hashPath,
                name: cacheItem.name
            ) {
                return (cacheItem, path)
            } else {
                return nil
            }
        })

        return results
    }

    public func store(
        _ items: [CacheStorableItem: [AbsolutePath]],
        cacheCategory: RemoteCacheCategory
