import FileSystem
import Foundation
import Path
import TuistAlert
import TuistCache
import TuistCore
import TuistLogging
import TuistServer
import TuistSupport
import XcodeGraph

enum CacheLocalStorageError: FatalError, Equatable {
    case compiledArtifactNotFound(hash: String)

    var type: ErrorType {
        switch self {
        case .compiledArtifactNotFound: return .abort
        }
    }

    var description: String {
        switch self {
        case let .compiledArtifactNotFound(hash):
            return "xcframework with hash '\(hash)' not found in the local cache"
        }
    }
}

public struct CacheLocalStorage: CacheStoring {
    // MARK: - Attributes

    private let cacheDirectoriesProvider: CacheDirectoriesProviding
    private let artifactSigner: ArtifactSigning
    private let fileHandler: FileHandling
    private let fileSystem: FileSysteming

    // MARK: - Init

    public init(cacheDirectoriesProvider: CacheDirectoriesProviding) {
        self.init(
            cacheDirectoriesProvider: cacheDirectoriesProvider,
            artifactSigner: ArtifactSigner(),
            fileHandler: FileHandler.shared,
            fileSystem: FileSystem()
        )
    }

    init(
        cacheDirectoriesProvider: CacheDirectoriesProviding,
        artifactSigner: ArtifactSigning,
        fileHandler: FileHandling,
        fileSystem: FileSysteming
    ) {
        self.cacheDirectoriesProvider = cacheDirectoriesProvider
        self.artifactSigner = artifactSigner
        self.fileHandler = fileHandler
        self.fileSystem = fileSystem
    }

    // MARK: - CacheStoring

    public func fetch(
        _ items: Set<CacheStorableItem>,
        cacheCategory: RemoteCacheCategory
    ) async throws -> [CacheItem: AbsolutePath] {
        let results = Dictionary(uniqueKeysWithValues: try await Array(items).concurrentCompactMap { item -> (
            CacheItem,
            AbsolutePath
        )? in
            let cacheDirectory = try cacheDirectory(for: cacheCategory)
            let hashPath = cacheDirectory.appending(component: item.hash)
            guard try await isValidAndExists(
                hashPath: hashPath,
                name: item.name,
                cacheCategory: cacheCategory
            ) else {
                return nil
            }

            var buildDuration: Double?
            switch cacheCategory {
            case .binaries:
                let metadataFilePath = hashPath.appending(component: "Metadata.plist")
                if try await fileSystem.exists(metadataFilePath) {
                    let metadata: CacheStorableItemMetadata? = try? await fileSystem.readPlistFile(at: metadataFilePath)
                    buildDuration = metadata?.time
                }
            case .selectiveTests:
                break
            }

            let cacheItem = CacheItem(
                name: item.name,
                hash: item.hash,
                source: .local,
                cacheCategory: cacheCategory,
                buildDuration: buildDuration
            )

            if cacheCategory == .selectiveTests {
                return try await fileSystem.exists(hashPath) ? (cacheItem, hashPath) : nil
            }

            if let path = try await lookupCompiledArtifact(
                directory: hashPath,
                name: cacheItem.name
            ) {
                return (cacheItem, path)
            } else {
                return nil
            }
        })

        return results
    }

    public func store(
        _ items: [CacheStorableItem: [AbsolutePath]],
        cacheCategory: RemoteCacheCategory
    ) async throws -> [CacheStorableItem] {
        let cacheDirectory = try cacheDirectory(for: cacheCategory)
        if try await !fileSystem.exists(cacheDirectory) {
            try await fileSystem.makeDirectory(at: cacheDirectory)
        }

        for item in items {
            let hashFolder = cacheDirectory.appending(component: item.key.hash)

            if try await !fileSystem.exists(hashFolder) {
                try await fileSystem.makeDirectory(at: hashFolder)
                try artifactSigner.sign(hashFolder)
            }

            for sourcePath in item.value {
                let destinationPath = hashFolder.appending(component: sourcePath.basename)
                if try await fileSystem.exists(destinationPath) {
                    try await fileSystem.remove(destinationPath)
                }
                try await fileSystem.copy(sourcePath, to: destinationPath)
                try artifactSigner.sign(destinationPath)
            }
        }

        return Array(items.keys)
    }

    // MARK: - Fileprivate

    fileprivate func cacheDirectory(for category: RemoteCacheCategory) throws -> AbsolutePath {
        switch category {
        case .binaries:
            return try cacheDirectoriesProvider.cacheDirectory(for: .binaries)
        case .selectiveTests:
            return try cacheDirectoriesProvider.cacheDirectory(for: .selectiveTests)
        }
    }

    fileprivate func isValidAndExists(
        hashPath: AbsolutePath,
        name: String,
        cacheCategory: RemoteCacheCategory
    ) async throws -> Bool {
        let exists: Bool
        var isValid = false

        switch cacheCategory {
        case .binaries:
            exists = try await lookupCompiledArtifact(
                directory: hashPath,
                name: name
            ) != nil
        case .selectiveTests:
            exists = try await fileSystem.exists(hashPath)
        }
        if cacheCategory == .binaries {
            isValid = try await areAllArtifactSignaturesValid(directory: hashPath)
        } else if cacheCategory == .selectiveTests {
            isValid = try artifactSigner.isValid(hashPath)
        } else {
            isValid = false
        }

        if exists, !isValid {
            AlertController.current.warning(
                .alert(
                    "Tuist Cache requires using the Tuist-provided server for fine-grained reading and persistence of cache binaries. See how you can use it by following the \(.link(title: "docs", href: "https://docs.tuist.dev/en/guides/develop/cache"))."
                )
            )
        }

        return exists && isValid
    }

    fileprivate func lookupCompiledArtifact(
        directory: AbsolutePath,
        name: String
    ) async throws -> AbsolutePath? {
        if let filePath = try await fileSystem.glob(
            directory: directory,
            include: ["\(name).{framework,xcframework,bundle,macro}"]
        ).collect()
            .first { return filePath }
        return nil
    }

    fileprivate func areAllArtifactSignaturesValid(directory: AbsolutePath) async throws -> Bool {
        try await fileSystem.glob(directory: directory, include: ["*"]).collect().allSatisfy { path in
            try artifactSigner.isValid(path)
        }
    }

    static func clean(
        fileSystem: FileSysteming,
        cacheDirectoriesProvider: CacheDirectoriesProviding,
        maxAge: TimeInterval = 7 * 24 * 60 * 60,
        maxEntries: Int = 200
    ) async throws {
        let binariesDirectory = try cacheDirectoriesProvider.cacheDirectory(for: .binaries)
        guard try await fileSystem.exists(binariesDirectory) else { return }

        let cutoffDate = Date().addingTimeInterval(-maxAge)

        var entriesWithDates: [(path: AbsolutePath, modificationDate: Date)] = []

        for entryPath in try await fileSystem.glob(directory: binariesDirectory, include: ["*"]).collect() {
            guard let modificationDate = try FileManager.default.attributesOfItem(
                atPath: entryPath.pathString
            )[.modificationDate] as? Date else { continue }

            if modificationDate < cutoffDate {
                try await fileSystem.remove(entryPath)
            } else {
                entriesWithDates.append((path: entryPath, modificationDate: modificationDate))
            }
        }

        if entriesWithDates.count > maxEntries {
            entriesWithDates.sort { $0.modificationDate > $1.modificationDate }
            let entriesToRemove = entriesWithDates.dropFirst(maxEntries)
            for entry in entriesToRemove {
                try await fileSystem.remove(entry.path)
            }
        }
    }
}

