 12→enum CacheLocalStorageError: FatalError, Equatable {
 13→    case compiledArtifactNotFound(hash: String)
 14→
 15→    var type: ErrorType {
 16→        switch self {
 17→        case .compiledArtifactNotFound: return .abort
 18→        }
 19→    }
 20→
 21→    var description: String {
 22→        switch self {
 23→        case let .compiledArtifactNotFound(hash):
 24→            return "xcframework with hash '\(hash)' not found in the local cache"
 25→        }
 26→    }
 27→}
 28→
 29→public struct CacheLocalStorage: CacheStoring {
 30→    // MARK: - Attributes
 31→
 32→    private let cacheDirectoriesProvider: CacheDirectoriesProviding
 33→    private let artifactSigner: ArtifactSigning
 34→    private let fileHandler: FileHandling
 35→    private let fileSystem: FileSysteming
 36→
 37→    // MARK: - Init
 38→
 39→    public init(cacheDirectoriesProvider: CacheDirectoriesProviding) {
 40→        self.init(
 41→            cacheDirectoriesProvider: cacheDirectoriesProvider,
 42→            artifactSigner: ArtifactSigner(),
 43→            fileHandler: FileHandler.shared,
 44→            fileSystem: FileSystem()
 45→        )
 46→    }
 47→
 48→    init(
 49→        cacheDirectoriesProvider: CacheDirectoriesProviding,
 50→        artifactSigner: ArtifactSigning,
 51→        fileHandler: FileHandling,
 52→        fileSystem: FileSysteming
 53→    ) {
 54→        self.cacheDirectoriesProvider = cacheDirectoriesProvider
 55→        self.artifactSigner = artifactSigner
 56→        self.fileHandler = fileHandler
 57→        self.fileSystem = fileSystem
 58→    }
 59→
 60→    // MARK: - CacheStoring
 61→
 62→    public func fetch(
 63→        _ items: Set<CacheStorableItem>,
 64→        cacheCategory: RemoteCacheCategory
 65→    ) async throws -> [CacheItem: AbsolutePath] {
 66→        let results = Dictionary(uniqueKeysWithValues: try await Array(items).concurrentCompactMap { item -> (
 67→            CacheItem,
 68→            AbsolutePath
 69→        )? in
 70→            let cacheDirectory = try cacheDirectory(for: cacheCategory)
 71→            let hashPath = cacheDirectory.appending(component: item.hash)
 72→            guard try await isValidAndExists(
 73→                hashPath: hashPath,
 74→                name: item.name,
 75→                cacheCategory: cacheCategory
 76→            ) else {
 77→                return nil
 78→            }
 79→
 80→            var buildDuration: Double?
 81→            switch cacheCategory {
 82→            case .binaries:
 83→                let metadataFilePath = hashPath.appending(component: "Metadata.plist")
 84→                if try await fileSystem.exists(metadataFilePath) {
 85→                    let metadata: CacheStorableItemMetadata? = try? await fileSystem.readPlistFile(at: metadataFilePath)
 86→                    buildDuration = metadata?.time
 87→                }
 88→            case .selectiveTests:
 89→                break
 90→            }
 91→
 92→            let cacheItem = CacheItem(
 93→                name: item.name,
 94→                hash: item.hash,
 95→                source: .local,
 96→                cacheCategory: cacheCategory,
 97→                buildDuration: buildDuration
 98→            )
 99→
100→            if cacheCategory == .selectiveTests {
101→                return try await fileSystem.exists(hashPath) ? (cacheItem, hashPath) : nil
102→            }
103→
104→            if let path = try await lookupCompiledArtifact(
105→                directory: hashPath,
106→                name: cacheItem.name
107→            ) {
108→                try? FileManager.default.setAttributes(
109→                    [.modificationDate: Date()],
110→                    ofItemAtPath: hashPath.pathString
111→                )
112→                return (cacheItem, path)
113→            } else {
114→                return nil
115→            }
116→        })
117→
118→        return results
119→    }
120→
121→    public func store(
122→        _ items: [CacheStorableItem: [AbsolutePath]],
123→        cacheCategory: RemoteCacheCategory
124→    ) async throws -> [CacheStorableItem] {
125→        let cacheDirectory = try cacheDirectory(for: cacheCategory)
126→        if try await !fileSystem.exists(cacheDirectory) {
127→            try await fileSystem.makeDirectory(at: cacheDirectory)
128→        }
129→
130→        for item in items {
131→            let hashFolder = cacheDirectory.appending(component: item.key.hash)
132→
133→            if try await !fileSystem.exists(hashFolder) {
134→                try await fileSystem.makeDirectory(at: hashFolder)
135→                try artifactSigner.sign(hashFolder)
136→            }
137→
138→            for sourcePath in item.value {
139→                let destinationPath = hashFolder.appending(component: sourcePath.basename)
140→                if try await fileSystem.exists(destinationPath) {
141→                    try await fileSystem.remove(destinationPath)
142→                }
143→                try await fileSystem.copy(sourcePath, to: destinationPath)
144→                try artifactSigner.sign(destinationPath)
145→            }
146→        }
147→
148→        return Array(items.keys)
149→    }
150→
151→    // MARK: - Fileprivate
152→
153→    fileprivate func cacheDirectory(for category: RemoteCacheCategory) throws -> AbsolutePath {
154→        switch category {
155→        case .binaries:
156→            return try cacheDirectoriesProvider.cacheDirectory(for: .binaries)
157→        case .selectiveTests:
158→            return try cacheDirectoriesProvider.cacheDirectory(for: .selectiveTests)
159→        }
160→    }
161→
162→    fileprivate func isValidAndExists(
163→        hashPath: AbsolutePath,
164→        name: String,
165→        cacheCategory: RemoteCacheCategory
166→    ) async throws -> Bool {
167→        let exists: Bool
168→        var isValid = false
169→
170→        switch cacheCategory {
171→        case .binaries:
172→            exists = try await lookupCompiledArtifact(
173→                directory: hashPath,
174→                name: name
175→            ) != nil
176→        case .selectiveTests:
177→            exists = try await fileSystem.exists(hashPath)
178→        }
179→        if cacheCategory == .binaries {
180→            isValid = try await areAllArtifactSignaturesValid(directory: hashPath)
181→        } else if cacheCategory == .selectiveTests {
182→            isValid = try artifactSigner.isValid(hashPath)
183→        } else {
184→            isValid = false
185→        }
186→
187→        if exists, !isValid {
188→            AlertController.current.warning(
189→                .alert(
190→                    "Tuist Cache requires using the Tuist-provided server for fine-grained reading and persistence of cache binaries. See how you can use it by following the \(.link(title: "docs", href: "https://docs.tuist.dev/en/guides/develop/cache"))."
191→                )
192→            )
193→        }
194→
195→        return exists && isValid
196→    }
197→
198→    fileprivate func lookupCompiledArtifact(
199→        directory: AbsolutePath,
200→        name: String
201→    ) async throws -> AbsolutePath? {
202→        if let filePath = try await fileSystem.glob(
203→            directory: directory,
204→            include: ["\(name).{framework,xcframework,bundle,macro}"]
205→        ).collect()
206→            .first { return filePath }
207→        return nil
208→    }
209→
210→    fileprivate func areAllArtifactSignaturesValid(directory: AbsolutePath) async throws -> Bool {
211→        try await fileSystem.glob(directory: directory, include: ["*"]).collect().allSatisfy { path in
212→            try artifactSigner.isValid(path)
213→        }
214→    }
215→
216→    static func clean(
217→        fileSystem: FileSysteming,
218→        cacheDirectoriesProvider: CacheDirectoriesProviding,
219→        maxAge: TimeInterval = 7 * 24 * 60 * 60,
220→        maxEntries: Int = 200
221→    ) async throws {
222→        let binariesDirectory = try cacheDirectoriesProvider.cacheDirectory(for: .binaries)
223→        guard try await fileSystem.exists(binariesDirectory) else { return }
224→
225→        let cutoffDate = Date().addingTimeInterval(-maxAge)
226→
227→        var entriesWithDates: [(path: AbsolutePath, modificationDate: Date)] = []
228→
229→        for entryPath in try await fileSystem.glob(directory: binariesDirectory, include: ["*"]).collect() {
230→            guard let modificationDate = try FileManager.default.attributesOfItem(
231→                atPath: entryPath.pathString
