107→open class TuistTestCase: XCTestCase {
108→    fileprivate var temporaryDirectory: TemporaryDirectory!
109→
110→    public var fileHandler: MockFileHandler!
111→
112→    override open func setUp() {
113→        super.setUp()
114→
115→        // FileHandler
116→        fileHandler = MockFileHandler(temporaryDirectory: { try self.temporaryPath() })
117→        FileHandler._shared.mutate { $0 = fileHandler }
118→    }
119→
120→    override open func tearDown() {
121→        temporaryDirectory = nil
122→        super.tearDown()
123→    }
124→
125→    public func temporaryPath() throws -> AbsolutePath {
126→        if temporaryDirectory == nil {
127→            temporaryDirectory = try TemporaryDirectory(removeTreeOnDeinit: true)
128→        }
129→        return temporaryDirectory.path
130→    }
131→
132→    @discardableResult
133→    public func createFiles(_ files: [String], content: String? = nil) async throws -> [AbsolutePath] {
134→        let temporaryPath = try temporaryPath()
135→        let fileSystem = FileSystem()
136→        let paths = try files.map { temporaryPath.appending(try RelativePath(validating: $0)) }
137→
138→        for item in paths {
139→            if try await !fileSystem.exists(item.parentDirectory, isDirectory: true) {
140→                try await fileSystem.makeDirectory(at: item.parentDirectory)
141→            }
142→            if try await fileSystem.exists(item) {
143→                try await fileSystem.remove(item)
144→            }
145→            if let content {
146→                try await fileSystem.writeText(content, at: item)
147→            } else {
148→                try await fileSystem.touch(item)
149→            }
150→        }
151→        return paths
152→    }
153→
154→    @discardableResult
155→    public func createFolders(_ folders: [String]) throws -> [AbsolutePath] {
156→        let temporaryPath = try temporaryPath()
157→        let fileHandler = FileHandler.shared
158→        let paths = try folders.map { temporaryPath.appending(try RelativePath(validating: $0)) }
159→        for path in paths {
160→            try fileHandler.createFolder(path)
161→        }
162→        return paths
163→    }
164→
165→    public func XCTAssertBetterEqual<T: Equatable>(
166→        _ received: @autoclosure () throws -> T,
167→        _ expected: @autoclosure () throws -> T,
168→        file: StaticString = #filePath,
169→        line: UInt = #line
170→    ) {
171→        do {
172→            let expected = try expected()
173→            let received = try received()
174→            XCTAssertTrue(
