@preconcurrency import FileSystem
import Foundation
import Mockable
import Path
import TuistEnvironment
import TuistSupport

/// Protocol for storing and retrieving CAS output metadata
@Mockable
public protocol CASOutputMetadataStoring: Sendable {
    /// Store metadata for a CAS output identified by CAS ID
    func storeMetadata(_ metadata: CASOutputMetadata, for casID: String) async throws

    /// Retrieve metadata for a CAS output
    func metadata(for casID: String) async throws -> CASOutputMetadata?
}

public struct CASOutputMetadataStore: CASOutputMetadataStoring {
    private let fileSystem: FileSysteming

    public init(fileSystem: FileSysteming = FileSystem()) {
        self.fileSystem = fileSystem
    }

    public func storeMetadata(_ metadata: CASOutputMetadata, for casID: String) async throws {
        let casDirectory = Environment.current.stateDirectory.appending(component: "cas")
        try await fileSystem.makeDirectory(at: casDirectory)

        let sanitizedCasID = sanitizeCasID(casID)
        let metadataFilePath = casDirectory.appending(component: "\(sanitizedCasID).json")

        let encoder = JSONEncoder()
        let jsonData = try encoder.encode(metadata)
        let jsonString = String(data: jsonData, encoding: .utf8)!

        if try await fileSystem.exists(metadataFilePath) {
            try await fileSystem.remove(metadataFilePath)
        }

        try await fileSystem.writeText(jsonString, at: metadataFilePath)
    }

    public func metadata(for casID: String) async throws -> CASOutputMetadata? {
        let casDirectory = Environment.current.stateDirectory.appending(component: "cas")
        let sanitizedCasID = sanitizeCasID(casID)
        let metadataFilePath = casDirectory.appending(component: "\(sanitizedCasID).json")

        guard try await fileSystem.exists(metadataFilePath) else {
            return nil
        }

        let jsonString = try await fileSystem.readTextFile(at: metadataFilePath)
        let jsonData = jsonString.data(using: .utf8)!

        let decoder = JSONDecoder()
        return try decoder.decode(CASOutputMetadata.self, from: jsonData)
    }

    // MARK: - Private Methods

    private func sanitizeCasID(_ casID: String) -> String {
        // Replace any characters that aren't filesystem-safe with underscores
        return casID.replacingOccurrences(of: "/", with: "_")
            .replacingOccurrences(of: ":", with: "_")
            .replacingOccurrences(of: "~", with: "_")
    }
}

@preconcurrency import FileSystem
import Foundation
import Mockable
import Path
import TuistEnvironment
import TuistSupport

public enum KeyValueOperationType: String, Codable {
    case read
    case write
}

/// Protocol for storing and retrieving KeyValue metadata
@Mockable
public protocol KeyValueMetadataStoring: Sendable {
    /// Store metadata for a KeyValue operation identified by cache key
    func storeMetadata(_ metadata: KeyValueMetadata, for cacheKey: String, operationType: KeyValueOperationType) async throws

    /// Retrieve metadata for a KeyValue operation
    func metadata(for cacheKey: String, operationType: KeyValueOperationType) async throws -> KeyValueMetadata?
}

public struct KeyValueMetadataStore: KeyValueMetadataStoring {
    private let fileSystem: FileSysteming

    public init(fileSystem: FileSysteming = FileSystem()) {
        self.fileSystem = fileSystem
    }

    public func storeMetadata(
        _ metadata: KeyValueMetadata,
        for cacheKey: String,
        operationType: KeyValueOperationType
    ) async throws {
        let keyValueDirectory = Environment.current.stateDirectory
            .appending(component: "keyvalue")
            .appending(component: operationType.rawValue)
        try await fileSystem.makeDirectory(at: keyValueDirectory)

        let sanitizedKey = sanitizeCacheKey(cacheKey)
        let metadataFilePath = keyValueDirectory.appending(component: "\(sanitizedKey).json")

        if try await fileSystem.exists(metadataFilePath) {
            try await fileSystem.remove(metadataFilePath)
        }

        try await fileSystem.writeAsJSON(metadata, at: metadataFilePath)
    }

    public func metadata(for cacheKey: String, operationType: KeyValueOperationType) async throws -> KeyValueMetadata? {
        let keyValueDirectory = Environment.current.stateDirectory
            .appending(component: "keyvalue")
            .appending(component: operationType.rawValue)
        let sanitizedKey = sanitizeCacheKey(cacheKey)
        let metadataFilePath = keyValueDirectory.appending(component: "\(sanitizedKey).json")

        guard try await fileSystem.exists(metadataFilePath) else {
            return nil
        }

        return try await fileSystem.readJSONFile(at: metadataFilePath)
    }

    // MARK: - Private Methods

    private func sanitizeCacheKey(_ cacheKey: String) -> String {
        // Replace any characters that aren't filesystem-safe with underscores
        return cacheKey.replacingOccurrences(of: "/", with: "_")
            .replacingOccurrences(of: ":", with: "_")
            .replacingOccurrences(of: "~", with: "_")
    }
}

@preconcurrency import FileSystem
import Foundation
import Mockable
import Path
import TuistEnvironment
import TuistSupport

/// Protocol for storing and retrieving CAS node ID to checksum mappings
@Mockable
public protocol CASNodeStoring: Sendable {
    /// Store a mapping between a node ID and checksum hex
    func storeNode(_ nodeID: String, checksum: String) async throws

    /// Retrieve checksum hex for a given node ID
    func checksum(for nodeID: String) async throws -> String?
}

public struct CASNodeStore: CASNodeStoring {
    private let fileSystem: FileSysteming

    public init(fileSystem: FileSysteming = FileSystem()) {
        self.fileSystem = fileSystem
    }

    public func storeNode(_ nodeID: String, checksum: String) async throws {
        let nodesDirectory = Environment.current.stateDirectory.appending(component: "nodes")
        try await fileSystem.makeDirectory(at: nodesDirectory)

        let sanitizedNodeID = sanitizeNodeID(nodeID)
        let nodeFilePath = nodesDirectory.appending(component: sanitizedNodeID)

        try await fileSystem.writeText(checksum, at: nodeFilePath, encoding: .utf8, options: Set([.overwrite]))
    }

    public func checksum(for nodeID: String) async throws -> String? {
        let nodesDirectory = Environment.current.stateDirectory.appending(component: "nodes")
        let sanitizedNodeID = sanitizeNodeID(nodeID)
        let nodeFilePath = nodesDirectory.appending(component: sanitizedNodeID)

        guard try await fileSystem.exists(nodeFilePath) else {
            return nil
        }

        return try await fileSystem.readTextFile(at: nodeFilePath)
    }

    // MARK: - Private Methods

    private func sanitizeNodeID(_ nodeID: String) -> String {
        // Replace any characters that aren't filesystem-safe with underscores
        return nodeID.replacingOccurrences(of: "/", with: "_")
            .replacingOccurrences(of: ":", with: "_")
    }
}

import FileSystem
import Foundation
import Path
import TuistEnvironment
import TuistLogging
import TuistSupport

/// Manages CLI session directories containing logs and network recordings.
///
/// Session directories are stored at `$XDG_STATE_HOME/tuist/sessions/<UUID>/`
/// and contain:
/// - `logs.txt`: The text log file for the session
/// - `network.har`: HTTP Archive file containing all network requests/responses
public struct SessionController {
    private let fileSystem: FileSystem

    public init(fileSystem: FileSystem = FileSystem()) {
        self.fileSystem = fileSystem
    }

    /// Sets up a new CLI session with logging and network recording.
    /// - Parameter stateDirectory: The base state directory (typically `$XDG_STATE_HOME/tuist`).
    /// - Returns: A tuple containing the logger handler factory and the session paths.
    public func setup(
        stateDirectory: AbsolutePath
    ) async throws -> (@Sendable (String) -> any LogHandler, SessionPaths) {
        let sessionPaths = try await createSessionDirectory(stateDirectory: stateDirectory)

        let machineReadableCommands = [DumpCommand.self]
        // swiftformat:disable all
        let isCommandMachineReadable =
            CommandLine.arguments.count > 1
            && machineReadableCommands.map { $0._commandName }.contains(CommandLine.arguments[1])
        // swiftformat:enable all
        let loggingConfig =
            if isCommandMachineReadable || CommandLine.arguments.contains("--json") {
                LoggingConfig(
                    loggerType: .json,
                    verbose: Environment.current.isVerbose
                )
            } else {
                LoggingConfig.default()
            }

        let loggerHandler = try Logger.defaultLoggerHandler(
            config: loggingConfig, logFilePath: sessionPaths.logFilePath
        )

        return (loggerHandler, sessionPaths)
    }

    /// Schedules best-effort cleanup of old session directories.
    public func scheduleMaintenance(stateDirectory: AbsolutePath) {
        Task.detached(priority: .background) { [fileSystem] in
            try? await Self.clean(fileSystem: fileSystem, stateDirectory: stateDirectory)
        }
    }

    /// Creates a new session directory and returns the paths for logs and network recording.
    /// - Parameters:
    ///   - stateDirectory: The base state directory (typically `$XDG_STATE_HOME/tuist`).
    ///   - sessionId: Optional session ID. If nil, a new UUID will be generated.
    /// - Returns: The session paths.
    private func createSessionDirectory(
        stateDirectory: AbsolutePath,
        sessionId: String? = nil
    ) async throws -> SessionPaths {
        let id = sessionId ?? UUID().uuidString
        let sessionDirectory = stateDirectory.appending(components: ["sessions", id])

        if !(try await fileSystem.exists(sessionDirectory)) {
            try await fileSystem.makeDirectory(at: sessionDirectory)
        }

        let logFilePath = sessionDirectory.appending(component: "logs.txt")
        let networkFilePath = sessionDirectory.appending(component: "network.har")

        try await fileSystem.touch(logFilePath)

        return SessionPaths(
            sessionId: id,
            sessionDirectory: sessionDirectory,
            logFilePath: logFilePath,
            networkFilePath: networkFilePath
        )
    }

    /// Cleans up old session directories based on age and count limits.
    /// - Parameters:
    ///   - stateDirectory: The base state directory.
    ///   - maxAge: Maximum age of sessions to keep (default: 5 days).
    ///   - maxSessions: Maximum number of sessions to keep (default: 50).
    static func clean(
        fileSystem: FileSystem,
        stateDirectory: AbsolutePath,
        maxAge: TimeInterval = 5 * 24 * 60 * 60,
        maxSessions: Int = 50
    ) async throws {
        let sessionsDirectory = stateDirectory.appending(component: "sessions")
        guard try await fileSystem.exists(sessionsDirectory) else { return }

        let cutoffDate = Date().addingTimeInterval(-maxAge)

        var sessionsWithDates: [(path: AbsolutePath, creationDate: Date)] = []

        for sessionPath in try await fileSystem.glob(directory: sessionsDirectory, include: ["*"]).collect() {
            guard let creationDate = try FileManager.default.attributesOfItem(
                atPath: sessionPath.pathString
            )[.creationDate] as? Date else { continue }

            if creationDate < cutoffDate {
                try await fileSystem.remove(sessionPath)
            } else {
                sessionsWithDates.append((path: sessionPath, creationDate: creationDate))
            }
        }

        if sessionsWithDates.count > maxSessions {
            sessionsWithDates.sort { $0.creationDate > $1.creationDate }
            let sessionsToRemove = sessionsWithDates.dropFirst(maxSessions)
            for session in sessionsToRemove {
                try await fileSystem.remove(session.path)
            }
        }
    }
}

/// Paths for a CLI session.
public struct SessionPaths: Sendable {
    /// The unique session identifier.
    public let sessionId: String

    /// The session directory path.
    public let sessionDirectory: AbsolutePath

    /// The path to the log file.
    public let logFilePath: AbsolutePath

    /// The path to the HAR file for network recordings.
    public let networkFilePath: AbsolutePath

    public init(
        sessionId: String,
        sessionDirectory: AbsolutePath,
        logFilePath: AbsolutePath,
        networkFilePath: AbsolutePath
    ) {
        self.sessionId = sessionId
        self.sessionDirectory = sessionDirectory
        self.logFilePath = logFilePath
        self.networkFilePath = networkFilePath
    }
}

