defmodule TuistWeb.BillingLive do
  @moduledoc false
  use TuistWeb, :live_view
  use Noora

  alias Tuist.Accounts
  alias Tuist.Billing

  @impl true
  def mount(params, _uri, %{assigns: %{current_user: current_user, selected_account: selected_account}} = socket) do
    if Tuist.Authorization.authorize(:billing_update, current_user, selected_account) != :ok do
      raise TuistWeb.Errors.UnauthorizedError,
            dgettext("dashboard_account", "You are not authorized to perform this action.")
    end

    subscription = Billing.get_current_active_subscription(selected_account)

    plan =
      if is_nil(subscription) do
        :air
      else
        subscription.plan
      end

    current_month_remote_cache_hits_count = selected_account.current_month_remote_cache_hits_count

    estimated_next_payment =
      Billing.get_estimated_next_payment(%{
        current_month_remote_cache_hits_count: current_month_remote_cache_hits_count
      })

    next_charge_date =
      if is_nil(subscription) do
        dgettext("dashboard_account", "charged /per month")
      else
        dgettext("dashboard_account", "charged on %{next_charge_date}",
          next_charge_date:
            subscription.subscription_id
            |> Billing.get_subscription_current_period_end()
            |> Timex.format!("{Mfull} {D}")
        )
      end

    payment_method =
      with {:subscription, subscription} when not is_nil(subscription) <-
             {:subscription, subscription},
           {:payment_method_id, payment_method_id} when not is_nil(payment_method_id) <-
             {:payment_method_id, Billing.get_payment_method_id_from_subscription_id(subscription.subscription_id)},
           {:payment_method, payment_method} <-
             {:payment_method, Billing.get_payment_method_by_id(payment_method_id)} do
        payment_method
      else
        {:subscription, nil} -> nil
        {:payment_method_id, nil} -> nil
      end

    plan =
      case params["plan"] do
        "pro" -> :pro
        "air" -> :air
        _ -> plan
      end

    socket =
      socket
      |> assign(selected_tab: "billing")
      |> assign(:estimated_next_payment, estimated_next_payment)
      |> assign(:plan, plan)
      |> assign(:next_charge_date, next_charge_date)
      |> assign(:current_month_remote_cache_hits_count, current_month_remote_cache_hits_count)
      |> assign(:head_title, "#{dgettext("dashboard_account", "Billing")} · #{selected_account.name} · Tuist")
      |> assign(:payment_method, payment_method)

    {:ok, socket}
  end

  @impl true
  def handle_params(_params, uri, socket) do
    {:noreply, assign(socket, :uri, uri)}
  end

  @impl true
  def handle_event("change_plan", %{"plan" => plan}, socket) do
    plan = String.to_atom(plan)

    send(self(), :change_plan)

    {:noreply,
     socket
     |> assign(:new_plan, plan)
     |> push_event("close-modal", %{id: "billing-upgrade-modal"})}
  end

  def handle_event("close-billing-upgrade-modal", _, socket) do
    socket = push_event(socket, "close-modal", %{id: "billing-upgrade-modal"})
    {:noreply, socket}
  end

  @impl true
  def handle_info(:change_plan, %{assigns: %{new_plan: new_plan, selected_account: selected_account, uri: uri}} = socket) do
    socket =
      case Billing.update_plan(%{
             plan: new_plan,
             period: :monthly,
             account: Accounts.create_customer_when_absent(selected_account),
             success_url: uri <> "?plan=#{new_plan}"
           }) do
        {:ok, {:external_redirect, session_url}} ->
          redirect(socket, external: session_url)

        :ok ->
          socket
      end

    {
      :noreply,
      assign(socket, :plan, new_plan)
    }
  end

  attr :label, :string, required: true

  def plan_feature(assigns) do
    ~H"""
    <li>
      <div data-part="icon"><.check /></div>
      <span data-part="label">
        {@label}
      </span>
    </li>
    """
  end

  attr :id, :string, required: true
  attr :name, :string, required: true
  attr :description, :string, required: true
  attr :price, :string, required: true
  attr :price_subtitle, :string, default: nil
  attr :features, :list, required: true
  attr :most_popular, :boolean, default: false

  slot :action_button, required: true

  def pricing_card(assigns) do
    ~H"""
    <div class="tuist-pricing-card" data-most-popular={@most_popular}>
      <div :if={@most_popular} data-part="most-popular">
        <span data-part="label">
          {dgettext("dashboard_account", "Most Popular")}
        </span>
        <div data-part="bezel">
          <.notch_svg id={@id <> "-bezel"} />
        </div>
      </div>
      <div data-part="header">
        <span data-part="name">
          {@name}
        </span>
        <span data-part="description">
          {@description}
        </span>
      </div>
      <.line_divider />
      <div data-part="price-with-button">
        <div data-part="price">
          <span data-part="title">{@price}</span>
          <span data-part="subtitle">{@price_subtitle}</span>
        </div>
        {render_slot(@action_button)}
      </div>
      <ul data-part="feature-list">
        <%= for feature <- @features do %>
          <.plan_feature label={feature} />
        <% end %>
      </ul>
    </div>
    """
  end

  attr :id, :string, required: true
  attr :plan, :atom, required: true

  def pricing_cards(assigns) do
    ~H"""
    <.pricing_card
      id={@id <> "-air"}
      name={dgettext("dashboard_account", "Air")}
      description={dgettext("dashboard_account", "Get started with no credit card required")}
      price={dgettext("dashboard_account", "Free")}
      features={[
        dgettext("dashboard_account", "Free under tier limits"),
        dgettext("dashboard_account", "All features, no credit card required"),
        dgettext("dashboard_account", "Community support via forum")
      ]}
    >
      <:action_button>
        <.button
          :if={@plan == :air}
          label={dgettext("dashboard_account", "Current plan")}
          variant="secondary"
          disabled
        />
        <.button
          :if={@plan == :pro}
          label={dgettext("dashboard_account", "Downgrade")}
          variant="secondary"
          phx-click="change_plan"
          phx-value-plan={:air}
        />
      </:action_button>
    </.pricing_card>
    <.pricing_card
      most_popular
      id={@id <> "-pro"}
      name={dgettext("dashboard_account", "Pro")}
      description={dgettext("dashboard_account", "Usage-based pricing after free tier")}
      price={dgettext("dashboard_account", "$0")}
      price_subtitle={dgettext("dashboard_account", "and up")}
      features={[
        dgettext("dashboard_account", "Pay nothing if below free tier limits"),
        dgettext("dashboard_account", "Pay only for what you use per feature"),
        dgettext("dashboard_account", "Standard support via Slack and email")
      ]}
    >
      <:action_button>
        <.button
          :if={@plan == :pro}
          label={dgettext("dashboard_account", "Current plan")}
          variant="primary"
          disabled
        />
        <.button
          :if={@plan == :air}
          label={dgettext("dashboard_account", "Upgrade")}
          variant="primary"
          phx-click="change_plan"
          phx-value-plan={:pro}
        />
      </:action_button>
    </.pricing_card>
    <.pricing_card
      id={@id <> "-enterprise"}
      name={dgettext("dashboard_account", "Enterprise")}
      description={dgettext("dashboard_account", "Create your plan or self-host your instance")}
      price={dgettext("dashboard_account", "Custom")}
      features={[
        dgettext("dashboard_account", "Custom terms"),
        dgettext("dashboard_account", "On-premise"),
        dgettext("dashboard_account", "Priority support via shared Slack channel")
      ]}
    >
      <:action_button>
        <.button
          label={dgettext("dashboard_account", "Contact sales")}
          variant="secondary"
          href="mailto:contact@tuist.dev"
        />
      </:action_button>
    </.pricing_card>
    """
  end

